        function getYourNumber(props) {
            /// predict your date number
            function get_sum(num) {
                return [...num].map( item => {return (item*1 > 0)? item*1: 0 }).reduce( (add, a) => add + a);
            }
            let number = get_sum(props);

            while (number > 9) {
                number = get_sum(number.toString())
            }
            return number;
        };

